Button.ClientEvent:
var charge_type_no = view.get("#charge_type_no").get("value");
var charge_type_name = view.get("#charge_type_name").get("value");
var charge_mode = view.get("#charge_mode").get("value");

var parameter = {
	charge_type_no:charge_type_no,
	charge_type_name:charge_type_name,
	charge_mode:charge_mode
};

view.get("#dsChargeTypeSet").set("parameter",parameter).flushAsync();

ToolBarButton.ClientEvent:
var action = this.id("deleteAction");
dorado.MessageBox.confirm("真的要完成选中的任务吗？",function(){
	action.execute(function(){
		this.id("dsChargeTypeSet").flushAsync();
		//ds.getData("#").reset("processInstances");
	});
});

Button.ClientEvent:
var charge_type_no_rela = view.get("#charge_type_no_rela").get("value");
var room_no = view.get("#room_no").get("value");
var owner_id = view.get("#owner_id").get("value");

var parameter = {
	charge_type_no_rela:charge_type_no_rela,
	room_no:room_no,
	owner_id:owner_id
};

view.get("#dsChargeTypeRelaView").set("parameter",parameter).flushAsync();

ToolBarButton.ClientEvent:
var action = this.id("deleteRelaAction");
dorado.MessageBox.confirm("真的要完成选中的任务吗？",function(){
	action.execute(function(){
		this.id("dsChargeTypeRoomRela").flushAsync();
		//ds.getData("#").reset("processInstances");
	});
});

DataTree.ClientEvent:
var obj = self.get("currentEntity");
var level = self.get("currentNode").get("level");
if(level == 4){
	var data = {
		roomId:obj.get("roomId")
	};
	/*
	view.get("#noSelectTip").set("visible",false);
	view.get("#roomDetail").refreshData();
	$('#d_roomDetail').css("visibility","visible");
	$('#d_fjzl').css("visibility","visible");
	*/
	view.get("#dsOwner").set("parameter",data).flushAsync(function(res){	
		var ser = view.get("#dsChargeTypeRoomRela.data.current")
		ser.set("owner_name",res.current.get("ownerName"));	
		ser.set("owner_id",res.current.get("ownerId"));
		ser.set("room_no",res.current.get("roomNo"));
		ser.set("room_id",res.current.get("roomId"));
	});
	
	var dropDown = dorado.widget.DropDown.findDropDown(self);
	dropDown.close();
}
